(function () {
'use strict';

/*открытие меню по нажатию на кнопку в мобильной версии*/
const btnClickHandler = (evt) => {
  const navModal = document.querySelector(`.nav__block`);

  navModal.classList.toggle(`nav--show`);
};

/*функция подчеркивания навигации*/
const itemMouseOverHandler = (evt) => {
  const currentItem = evt.target;
  if (currentItem.classList.contains('nav__item')) {
    currentItem.classList.add(`nav__item--active`);
  }
  currentItem.addEventListener(`mouseout`, itemMouseOutHandler);
};

const itemMouseOutHandler = (evt)=> {
  evt.target.classList.remove(`nav__item--active`);
};

/*фото гироскутреров разной расцветки*/

const hyroPhotos = {'yellow': `https://giromagazin.ru/upload/iblock/8e0/8e0dbcdd4d7dbec16027d3e0dbad84f5.jpg`,
  'blue' : `http://jet-way.ru/image/cache//data/SmartBalance_K3/Blue-cosmos1-463x463.jpg`,
  'red' : `https://giromagazin.ru/upload/iblock/3d5/3d5d38935c001e055abc048cb7264ff2.jpg`,
  'grey' : `https://giromagazin.ru/upload/iblock/1b9/1b9c5ceb5cf85d92b83d886f6e2cc85e.png`,
  'black' : `img/photo1-desk.jpg`,
};

/*функция выбора цвета*/
const pictureHyro = document.querySelector(`.picture`);
const source1900 = pictureHyro.querySelector(`source`);
const source768 = pictureHyro.querySelector(`.source-2`);
const img = pictureHyro.querySelector(`img`);

const changePhoto = (evt) => {
  if(evt.target.name === `color`) {
    const id = evt.target.getAttribute(`id`);

    source1900.srcset = hyroPhotos[id];
    source768.srcset = hyroPhotos[id];
    img.src = hyroPhotos[id];
  }
};

const menuBtn = document.querySelector(`.menu__btn`);
menuBtn.addEventListener(`click`, btnClickHandler);

const listItems = document.querySelector(`.nav__list`);
listItems.addEventListener(`mouseover`, itemMouseOverHandler);

const listColors = document.querySelector(`.color__list`);
listColors.addEventListener(`click`, changePhoto);

}());

